            function edit_slider(id)
            {
                save_method = 'update';
                $('#form-upload')[0].reset(); // reset form on modals
                //Ajax Load data from ajax
                $.ajax({
                    url: "<?php echo site_url('Pasca_admin/ajax_edit/') ?>/" + id,
                    type: "POST",
                    dataType: "JSON",
                    success: function (data)
                    {
                    $('[name="id"]').val(data.id);
                    $('[name="picSebelum"]').val(data.pic);
                    $('[name="file"]').val(data.file);
                    $('[name="namePic"]').val(data.namePic);
                    $('[name="pricePic"]').val(data.pricePic);

                    $('#modal_form').modal('show'); // show bootstrap modal when complete loaded
                    $('.modal-title').text('Edit Slider'); // Set title to Bootstrap modal Title
                    },
                    error: function (jqXHR, textStatus, errorThrown)
                    {
                        alert('Error get data from ajax');
                    }
                });
            }

            function save(){
                var url;

                if (save_method == "add") {
                    url = "<?php echo site_url('Pasca_admin/sliderAdd') ?>";
                    var file = $('input[name=file]');
                    var fileToUpload = file[0].files[0];
                    var formData = new FormData();
                    formData.append("file", fileToUpload);
                    formData.append("namePic", $("#namePic").val());
                    formData.append("pricePic", $("#pricePic").val());
                    if (!fileToUpload && !$("#namePic").val() && !$("#namePic").val()) {
                         $.alert({
                            title: 'ERROR!',
                            content: 'Gagal Upload Data. Mohon untuk mengisi seluruh field dengan benar',
                            type: 'red',
                            typeAnimated: true
                        }); 
                    }else{
                    // now upload the file using $.ajax
                    $.ajax({
                        url: url,
                        type: 'post',
                        data:  formData,
                        //dataType: "json",
                        processData: false,
                        contentType: false,
                        success: function() {
                                $.alert({
                                    title: 'SUKSES!',
                                    content: 'Data berhasil di tambahkan.',
                                    type: 'green',
                                    typeAnimated: true,
                                    buttons: {
                                        ok: {
                                            text: 'OK',
                                            btnClass: 'btn-green',
                                            action: function () {
                                                $('#modal_form').modal('hide');
                                                location.reload();
                                            }
                                        }
                                    }
                                });
