                                $.alert({
                                    title: 'SUKSES!',
                                    content: 'Data berhasil di ubah.',
                                    type: 'green',
                                    typeAnimated: true,
                                    buttons: {
                                        ok: {
                                            text: 'OK',
                                            btnClass: 'btn-green',
                                            action: function () {
                                                $('#modal_form').modal('hide');
                                                location.reload();
                                            }
                                        }
                                    }
                                });
                            }
