                        var formData = new FormData();
                        formData.append("id", $("#id").val());
                        formData.append("file", fileToUpload);
                        formData.append("namePic", $("#namePic").val());
                        formData.append("picSebelum", $("#picSebelum").val());
                        formData.append("pricePic", $("#pricePic").val());
