                                            action: function () {
                                                $('#modal_form').modal('hide');
                                                location.reload();
                                            }
                                        }
                                    }
                                });
                            }
                        });
                    }
                } 
            }

            function delete_slider(id)
            {
                $.confirm({
                    title: 'Delete Slider?',
                    content: 'Apakah kamu yakin menghapus slider?',
                    type: 'red',
                    typeAnimated: true,
                    buttons: {
                        deleteUser: {
                            text: 'Delete',
                            btnClass: 'btn-red',
                            action: function () {
                                $.ajax({
                                        url: "<?php echo site_url('Pasca_admin/slider_delete') ?>/" + id,
                                        type: "POST",
                                        dataType: "JSON",
                                        success: function (data)
                                        {
                                            $.alert('Sukses delete slider!');
                                            location.reload();
                                        },
                                        error: function (jqXHR, textStatus, errorThrown)
                                        {
                                            alert('Error deleting data');
                                        }
