        <script>
            $(document).ready(function (){
               $('#summernote').summernote({
                    height: 300, // set editor height
                    minHeight: null, // set minimum height of editor
                    maxHeight: null, // set maximum height of editor
                    callbacks: {
                        onImageUpload: function(files) {
                           sendFile(files[0]);
                        }
                    }
                });
               
                $('#summernote2').summernote({
                    height: 300, // set editor height
                    minHeight: null, // set minimum height of editor
                    maxHeight: null, // set maximum height of editor
                    callbacks: {
                        onImageUpload: function(files) {
                           sendFile2(files[0]);
                        }
                    }
                });
                
                $('#table_id').DataTable({
                    "paging": true,
                    "lengthChange": true,
                    "searching": false,
                    "ordering": true,
                    "info": false,
                    "autoWidth": true
                });

                Dropzone.options.myAwesomeDropzone = {
                    url: "<?php echo site_url("Pasca_paket/upload")?>", // Set the url
                    autoProcessQueue: false,
                    uploadMultiple: true,
                    parallelUploads: 100,
                    addRemoveLinks: true,
                    maxFiles: 100,
                    // Dropzone settings
                    init: function() {
                        var myDropzone = this;
                        this.element.querySelector("button[type=submit]").addEventListener("click", function(e) {
                            e.preventDefault();
                            e.stopPropagation();
                            myDropzone.processQueue();
                        });
                        this.on("sendingmultiple", function(file, xhr, formData) {
                            formData.append('id_paket', $("#id_paket").val());
                        });
                        this.on("successmultiple", function(files, response) {
                        });
                        this.on("errormultiple", function(files, response) {
                        });
                    },
                    removedfile: function(file) {
                        var name = file.name;
                        $.ajax({
                            type: "post",
                            url: "<?php echo site_url("Pasca_paket/remove") ?>",
                            data: { file: name },
                            dataType: 'html'
                        });
                        // remove the thumbnail
                        var previewElement;
                        return (previewElement = file.previewElement) != null ? (previewElement.parentNode.removeChild(file.previewElement)) : (void 0);
                    }
                }
            });

            function save1(){
                function toAngka(rp){return parseInt(rp.replace(/,.*|\D/g,''),10)}
                var angka = toAngka($("#harga_paket").val());

                var formData = new FormData();
                formData.append('id_paket', $("#id_paket").val());
                formData.append("nama_paket", $("#nama_paket").val());
                formData.append("lokasi_paket", $("#lokasi_paket").val());
                formData.append("harga_paket", angka);
                formData.append("deskripsi_paket", $("#deskripsi_paket").val());
                formData.append("syarat", $('.click2edit2').summernote('code'));
                formData.append("itenary", $('.click2edit').summernote('code'));
                //End of Foto
                $.ajax({
                    url: "<?php echo site_url("Pasca_paket/upload_data")?>",
                    type: 'post',
                    data: formData,
                    processData: false,
                    contentType: false,
                    success: function(data) {
                        $.alert({
                            title: 'SUKSES!',
                            content: 'Data berhasil di tambahkan.',
                            type: 'green',
                            typeAnimated: true,
                            buttons: {
                                ok: {
                                    text: 'OK',
                                    btnClass: 'btn-green',
                                    action: function () {
                                        location.href = "<?php echo site_url('Pasca_paket');?>"
                                    }
                                }
                            }
                        });
                    }
                });
            }
            function sendFile(file){
                data = new FormData();
                data.append("file", file);
                data.append("id_paket", $("#id_paket").val());
                $.ajax({
                    data: data,
                    type: "POST",
                    url: "<?php echo site_url("Pasca_paket/upload_supernote")?>",
                    cache: false,
                    contentType: false,
                    processData: false,
                    success: function(url) {
                        var imgNode = document.createElement("IMG");
                        imgNode.setAttribute("src", url);
                        $('#summernote').summernote('insertNode', imgNode);
                    },
                    error: function(data) {
                        console.log(data.responseText);
                    }
                });
            }

            function sendFile2(file){
                data = new FormData();
                data.append("file", file);
                data.append("id_paket", $("#id_paket").val());
                $.ajax({
                    data: data,
                    type: "POST",
                    url: "<?php echo site_url("Pasca_paket/upload_supernote")?>",
                    cache: false,
                    contentType: false,
                    processData: false,
                    success: function(url) {
                        var imgNode = document.createElement("IMG");
                        imgNode.setAttribute("src", url);
                        $('#summernote2').summernote('insertNode', imgNode);
                    },
                    error: function(data) {
                        console.log(data.responseText);
                    }
                });
            }
        </script>
