        <script>
            $(document).ready(function () {
                $('#table_id').DataTable({
                  "paging": true,
                  "lengthChange": true,
                  "searching": false,
                  "ordering": true,
                  "info": false,
                  "autoWidth": true
                });
            });

            function delete_paket($id_paket){
                $.confirm({
                    title: 'Delete Paket?',
                    content: 'Apakah kamu yakin menghapus paket?',
                    type: 'red',
                    typeAnimated: true,
                    buttons: {
                        deleteUser: {
                            text: 'Delete',
                            btnClass: 'btn-red',
                            action: function () {
                                var formData = new FormData();
                                formData.append("id_paket", $id_paket);
                                $.ajax({
                                        url: "<?php echo site_url("Pasca_paket/delete_paket")?>",
                                        type: 'post',
                                        data: formData,
                                        processData: false,
                                        contentType: false,
                                        success: function() {
                                            $.alert({
                                                title: 'SUKSES!',
                                                content: 'Data berhasil di hapus.',
                                                type: 'green',
                                                typeAnimated: true,
                                                buttons: {
                                                    ok: {
                                                        text: 'OK',
                                                        btnClass: 'btn-green',
                                                        action: function () {
                                                            location.reload();
                                                        }
                                                    }
                                                }
                                            });
                                        },
                                        error: function (jqXHR, textStatus, errorThrown)
                                        {
                                            onsole.log(data.responseText);
                                        console.log(data);
                                        }
                                    });
                            }
                        },
                        cancel: function () {
                            $.alert('Menghapus paket di batalkan');
                        }
                    }
                });   
            }
        </script>
